events.hide:
function() { 
  this.chart.update({ 
    annotations: [{
      labels: [{
        point: { x: 100, y: 20, xAxis: 0, yAxis: 0 },
        text: `Series hidden ${(this.chart.hideCount || 0) + 1} times`,
        style: { 
          color: '#FFFFFF',
          fontSize: '14px',
          padding: 10,
          backgroundColor: '#27AE60'
        }
      }]
    }]
  });
  this.chart.hideCount = (this.chart.hideCount || 0) + 1;
}
