visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: { type: 'column', backgroundColor: '#f9f9f9' },
    title: { 
        text: 'Synthetic Monthly Revenue (2023)',
        style: { color: '#1A5276', fontSize: '20px' }
    },
    subtitle: {
        text: 'On-screen: 700×500 | Exported: 1000×500 | YTD: +133%',
        floating: true,
        align: 'left',
        x: 60,
        y: 50,
        style: { color: '#2E4053'
    }
    },
    xAxis: {
        categories: ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'],
        labels: { style: { color: '#154360', fontSize: '12px' } },
        title: { text: 'Month', style: { fontWeight: 'bold' } }
    },
    yAxis: {
        title: { 
            text: 'Revenue (USD thousands)', 
            style: { color: '#154360', fontWeight: 'bold' }
        },
        labels: { format: '${value}k' }
    },
    series: [{
        name: 'Monthly Revenue',
        data: [150, 180, 220, 240, 260, 280, 250, 230, 270, 300, 320, 350],
        color: { linearGradient: { x1: 0, x2: 0, y1: 0, y2: 1 },
        stops: [[0, '#2ECC71'], [1, '#17A589']] }
    }],
    tooltip: {
        headerFormat: '<b>{point.x}</b><br/>',
        pointFormat: 'Revenue: <b>${point.y}k</b> (+{point.percentage:.0f}% vs Jan)'
    },
    exporting: {
        sourceWidth: 500,
        sourceHeight: 250,
        chartOptions: {
            subtitle: null,
            title: { text: '2023 Revenue Export View' }
        }
    }
});
