visualization_code.javascript:
Highcharts.chart('hc-container', {
    title: { text: 'Market Segment Value Distribution', style: { fontSize: '18px', color: '#333' } },
    colorAxis: {
        min: 2,
        max: 25,
        minColor: '#FFFFFF',
        maxColor: '#FF0000',
        labels: { format: '${value}M' }
    },
    series: [{
        type: 'treemap',
        layoutAlgorithm: 'squarified',
        data: [
            { name: 'Electronics', value: 25, colorValue: 25 },
            { name: 'Apparel', value: 20, colorValue: 20 },
            { name: 'Home & Kitchen', value: 18, colorValue: 18 },
            { name: 'Health & Beauty', value: 15, colorValue: 15 },
            { name: 'Automotive', value: 12, colorValue: 12 },
            { name: 'Sports Equipment', value: 8, colorValue: 8 },
            { name: 'Books', value: 2, colorValue: 2 }
        ],
        dataLabels: {
            enabled: true,
            format: '<b>{point.name}</b><br>${point.value}M',
            style: { textOutline: 'none', fontSize: '12px' }
        }
    }],
    plotOptions: {
        treemap: { borderWidth: 2, borderColor: '#333' }
    },
    credits: { enabled: false }
});
