visualization_code.javascript:
Highcharts.chart('polar-container', {
  chart: {
    polar: true,
    backgroundColor: '#f8f9fa'
  },
  title: {
    text: 'Dual-Pane Polar Analysis: Signal Patterns',
    style: { fontSize: '18px', color: '#2c3e50' }
  },
  pane: [{
    startAngle: -45,
    endAngle: 315,
    center: ['30%', '50%'],
    size: '55%',
    background: { backgroundColor: 'rgba(173, 216, 230, 0.1)' }
  }, {
    startAngle: 45,
    endAngle: 405,
    center: ['70%', '50%'],
    size: '55%',
    background: { backgroundColor: 'rgba(255, 182, 193, 0.1)' }
  }],
  xAxis: [{
    pane: 0,
    gridLineWidth: 1,
    tickInterval: 60,
    labels: { format: '{value}°', style: { color: '#2980b9' } }
  }, {
    pane: 1,
    gridLineWidth: 1,
    tickInterval: 45,
    labels: { format: '{value}°', style: { color: '#e74c3c' } }
  }],
  yAxis: [{
    pane: 0,
    gridLineInterpolation: 'circle',
    min: 0,
    max: 12,
    tickInterval: 3,
    labels: { enabled: false }
  }, {
    pane: 1,
    gridLineInterpolation: 'polygon',
    min: 0,
    max: 15,
    tickInterval: 5,
    labels: { format: '{value} dB', style: { fontWeight: 'bold' } }
  }],
  series: [{
    type: 'column',
    name: 'RF Signal Strength',
    data: [9.2, 7.1, 5.8, 4.3, 6.7, 8.9, 10.5, 3.4],
    pointPlacement: 'between',
    color: 'rgba(41, 128, 185, 0.8)',
    yAxis: 0,
    xAxis: 0
  }, {
    type: 'area',
    name: 'Noise Floor',
    data: [2.4, 11.7, 3.9, 9.2, 5.1, 13.6, 7.8, 4.5],
    fillOpacity: 0.4,
    color: 'rgba(231, 76, 60, 0.6)',
    yAxis: 1,
    xAxis: 1
  }],
  legend: {
    align: 'center',
    verticalAlign: 'bottom',
    itemStyle: { fontSize: '12px', color: '#34495e' }
  }
});
