events.render:
function () {

                const chart = this,
                    zones = [],
                    renderer = chart.renderer,
                    sdColors = [
                        Highcharts.color(
                            Highcharts.getOptions().colors[0]
                        ).brighten(0.3).get(),
                        Highcharts.color(
                            Highcharts.getOptions().colors[0]
                        ).brighten(0.15).get(),
                        Highcharts.getOptions().colors[0]
                    ];

                let zoneStops,
                    zoneStopsLen,
                    xAxis,
                    base;

                chart.series.forEach(function (s) {
                    if (s.type === 'bellcurve') {

                        xAxis = s.xAxis;
                        zoneStops = chart.zoneStops;
                        base = zoneStops[1] - zoneStops[0];
                        zoneStopsLen = zoneStops.length;

                        if (!s.options.zones) {
                            for (let i = 0; i < zoneStopsLen; i++) {
                                zones.push({
                                    value: zoneStops[i],
                                    color: i < zoneStopsLen / 2 ?
                                        sdColors[i] : sdColors[(zoneStopsLen - 1) - i]
                                });
                            }

                            s.update({
                                zones: zones
                            });
                        }

                        if (s.stDevLabelsGroup) {
                            s.stDevLabels.forEach(function (label, i) {
                                label.attr({
                                    x: xAxis.toPixels(
                                        zoneStops[i] -
                                         (base / 2)
                                    ) - (label.width / 2),
                                    y: chart.plotBox.y +
                                         s.yAxis.len - label.height
                                });
                            });
                        } else {
                            const percents = ['2.35%', '13.5%', '34.0%'];

                            s.stDevLabelsGroup = renderer.g(
                                'st-dev-labels'
                            ).attr({
                                zIndex: 5
                            }).add();
                            s.stDevLabels = [];

                            for (let i = 0; i < zoneStopsLen; i++) {
                                const label = renderer.label(
                                    i < zoneStopsLen / 2 ? percents[i] :
                                        percents[(zoneStopsLen - 1) - i],
                                    0,
                                    0
                                ).add(s.stDevLabelsGroup);

                                label.attr({
                                    x: xAxis.toPixels(
                                        zoneStops[i] -
                                         (base / 2)
                                    ) - (label.width / 2),
                                    y: chart.plotBox.y +
                                         s.yAxis.len - label.height
                                });
                                s.stDevLabels.push(label);
                            }
                        }

                    }
                });

            }
