visualization_code.JavaScript:
Highcharts.chart('container', {
    title: {
        text: 'Synthetic Process Metric Trend with Data Gap',
        style: { fontSize: '18px', color: '#2c3e50' }
    },
    xAxis: {
        title: { text: 'Measurement Interval', style: { color: '#34495e' } },
        labels: { style: { fontSize: '12px' } },
        tickInterval: 2,
        breaks: [{ from: 5, to: 10, breakSize: 1 }]
    },
    yAxis: {
        title: { text: 'Metric Value', style: { color: '#34495e' } },
        labels: { style: { fontSize: '12px' } }
    },
    legend: { align: 'right', verticalAlign: 'middle' },
    series: [{
        name: 'Process Efficiency',
        data: (() => {
            const data = [];
            let baseValue = 65;
            for (let x = 0; x < 20; x++) {
                data.push({
                    x: x,
                    y: baseValue + (Math.random() * 8 - 4) + (x * 1.7)
                });
            }
            return data;
        })()
    }],
    plotOptions: {
        series: {
            marker: { radius: 3 },
            color: '#3498db',
            gapSize: 1
        }
    },
    credits: { enabled: false }
});
